  if (msg.status !== 200) {
    alert("Error: " + msg.status);
    return;
  }
  alert("Payload: ", msg.payload);
}
var ctxlink = connect.byId("ctxlink");
var oauthtoken = connect.oauth.token();
ctxlink.onclick=function() {
  connect.client.ctx(callback, oauthtoken)};
}

